import { useQuery } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import Sidebar from "@/components/layout/Sidebar";
import RightSidebar from "@/components/layout/RightSidebar";
import ProductPost from "@/components/product/ProductPost";
import { Card, CardContent } from "@/components/ui/card";
import { Skeleton } from "@/components/ui/skeleton";
import { useEffect, useState } from "react";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";

// Define TypeScript interface for Product
interface Product {
  id: string;
  title: string;
  description: string;
  image: string;
  price: number;
  // Add other properties as needed
}

export default function Home() {
  const { isAuthenticated, isLoading: authLoading } = useAuth();
  const { toast } = useToast();
  const [shouldRedirect, setShouldRedirect] = useState(false);

  const {
    data: products,
    isLoading: productsLoading,
    error,
    refetch,
  } = useQuery<Product[]>({
    queryKey: ["/api/products"],
    retry: false,
    enabled: isAuthenticated, // Only fetch if authenticated
  });

  useEffect(() => {
    if (authLoading) return;

    if (!isAuthenticated) {
      toast({
        title: "Authentication required",
        description: "You need to log in to view this content",
        variant: "destructive",
      });
      setShouldRedirect(true);
    } else if (error) {
      if (isUnauthorizedError(error as Error)) {
        toast({
          title: "Session expired",
          description: "Please log in again",
          variant: "destructive",
        });
        setShouldRedirect(true);
      } else {
        toast({
          title: "Error loading products",
          description: "Please try again later",
          variant: "destructive",
        });
      }
    }

    if (shouldRedirect) {
      const timer = setTimeout(() => {
        window.location.href = "/api/login";
      }, 1500);
      return () => clearTimeout(timer);
    }
  }, [isAuthenticated, authLoading, error, toast, shouldRedirect]);

  if (authLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center space-y-4">
          <Skeleton className="h-12 w-12 rounded-full mx-auto" />
          <Skeleton className="h-6 w-48 mx-auto" />
          <Skeleton className="h-4 w-32 mx-auto" />
        </div>
      </div>
    );
  }

  if (!isAuthenticated || shouldRedirect) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-xl font-semibold mb-2">Redirecting to login...</h2>
          <p>Please wait while we redirect you to the login page.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="flex min-h-screen">
      <Sidebar />

      {/* Main content area with adjusted margins */}
      <div className="flex-1 ml-20 mr-64">
        <div className="flex justify-center">
          <div className="w-full max-w-2xl border-x border-gray-200 min-h-screen bg-white">
            {/* Header */}
            <div className="sticky top-0 bg-card/80 backdrop-blur-sm border-b border-border p-4" data-testid="feed-header">
              <div className="flex justify-between items-center">
                <h2 className="text-xl font-bold">Home</h2>
                {error && !isUnauthorizedError(error as Error) && (
                  <button 
                    onClick={() => refetch()}
                    className="text-sm px-3 py-1 bg-primary text-primary-foreground rounded-md hover:bg-primary/90"
                  >
                    Retry
                  </button>
                )}
              </div>
            </div>

            {/* Product Posts Feed */}
            <div className="divide-y divide-border" data-testid="product-feed">
              {productsLoading ? (
                <div className="space-y-6 p-6">
                  {[...Array(3)].map((_, i) => (
                    <Card key={i} className="p-6">
                      <CardContent className="space-y-4">
                        <div className="flex space-x-3">
                          <Skeleton className="w-10 h-10 rounded-full" />
                          <div className="space-y-2 flex-1">
                            <Skeleton className="h-4 w-1/3" />
                            <Skeleton className="h-4 w-full" />
                            <Skeleton className="h-4 w-2/3" />
                          </div>
                        </div>
                        <Skeleton className="h-48 w-full rounded-xl" />
                      </CardContent>
                    </Card>
                  ))}
                </div>
              ) : error && isUnauthorizedError(error as Error) ? (
                <div className="p-12 text-center">
                  <p className="text-destructive">Your session has expired. Redirecting to login...</p>
                </div>
              ) : products && products.length > 0 ? (
                products.map((product) => (
                  <ProductPost key={product.id} product={product} />
                ))
              ) : (
                <div className="p-12 text-center text-muted-foreground" data-testid="empty-feed">
                  <p className="mb-4">No products found.</p>
                  <p>Start following users or explore trending products!</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      <RightSidebar />
    </div>
  );
}

client/src/pages/messages.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { useState, useEffect, useMemo } from "react";
import { useAuth } from "@/hooks/useAuth";
import Sidebar from "@/components/layout/Sidebar";
import ChatArea from "@/components/chat/ChatArea";
import StartConversationModal from "@/components/messages/StartConversationModal";
import { Input } from "@/components/ui/input";
import { Skeleton } from "@/components/ui/skeleton";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { Button } from "@/components/ui/button";
import { FaPlus, FaSearch } from "react-icons/fa";

export default function Messages() {
  const { isAuthenticated, isLoading: authLoading } = useAuth();
  const { toast } = useToast();
  const [selectedUserId, setSelectedUserId] = useState<string | null>(null);
  const [showStartConversation, setShowStartConversation] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");

  const {
    data: conversations,
    isLoading,
    error,
  } = useQuery({
    queryKey: ["/api/messages/conversations"],
    retry: false,
  });

  useEffect(() => {
    if (!authLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, authLoading, toast]);

  useEffect(() => {
    if (error && isUnauthorizedError(error as Error)) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [error, toast]);

  if (authLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <Skeleton className="h-8 w-32" />
      </div>
    );
  }

  // Deduplicate conversations by userId
  const uniqueConversations = useMemo(() => {
    if (!conversations || !Array.isArray(conversations)) return [];
    const seen = new Set<string>();
    return conversations.filter((conv: any) => {
      if (seen.has(conv.userId)) return false;
      seen.add(conv.userId);
      return true;
    });
  }, [conversations]);

  // Filter conversations based on search query
  const filteredConversations = useMemo(() => {
    return uniqueConversations.filter((conversation: any) => {
      const fullName = `${conversation.user?.firstName || ""} ${
        conversation.user?.lastName || ""
      }`.toLowerCase();
      const lastMessage = conversation.lastMessage?.toLowerCase() || "";
      const query = searchQuery.toLowerCase();
      return fullName.includes(query) || lastMessage.includes(query);
    });
  }, [uniqueConversations, searchQuery]);

  return (
    <div className="flex min-h-screen bg-gray-100">
      <Sidebar />

      <div className="flex-1 ml-64">
        <div className="flex h-screen">
          {/* Conversations List */}
          <div
            className="w-80 border-r border-gray-300 bg-gray-50 shadow-lg"
            data-testid="conversations-list"
          >
            {/* Header with title and new chat button */}
            <div className="p-6 border-b border-gray-300 bg-white">
              <div className="flex items-center justify-between mb-4">
                <h2 className="text-2xl font-bold text-gray-900">Chats</h2>
                <Button
                  size="sm"
                  onClick={() => setShowStartConversation(true)}
                  className="bg-red-500 hover:bg-red-600 text-white rounded-full w-10 h-10 p-0 shadow-md"
                  data-testid="button-start-conversation"
                >
                  <FaPlus className="w-5 h-5" />
                </Button>
              </div>

              {/* Search Bar */}
              <div className="relative">
                <FaSearch className="absolute left-4 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
                <Input
                  placeholder="Search"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-12 py-3 border-gray-300 rounded-xl bg-gray-100 focus:bg-white focus:border-gray-400 shadow-sm"
                  data-testid="input-search-conversations"
                />
              </div>
            </div>

            {/* Conversations List */}
            <div className="overflow-y-auto bg-gray-50">
              {isLoading ? (
                <div className="space-y-2 p-2">
                  {[...Array(5)].map((_, i) => (
                    <div
                      key={i}
                      className="p-4 flex items-center space-x-4 bg-white rounded-xl mx-2 shadow-sm"
                    >
                      <Skeleton className="w-14 h-14 rounded-full" />
                      <div className="space-y-2 flex-1">
                        <Skeleton className="h-4 w-1/2" />
                        <Skeleton className="h-3 w-3/4" />
                      </div>
                    </div>
                  ))}
                </div>
              ) : filteredConversations.length > 0 ? (
                <div className="p-2 space-y-2">
                  {filteredConversations.map(
                    (conversation: any, index: number) => {
                      const unreadCount =
                        Math.random() > 0.7
                          ? Math.floor(Math.random() * 5) + 1
                          : 0;

                      return (
                        <div
                          key={`${conversation.userId}-${index}`}
                          className={`p-4 hover:bg-gray-100 transition-all duration-200 cursor-pointer rounded-xl mx-1 shadow-sm ${
                            selectedUserId === conversation.userId
                              ? "bg-blue-100 border-l-4 border-l-blue-500"
                              : "bg-white border-l-4 border-l-transparent"
                          }`}
                          onClick={() => setSelectedUserId(conversation.userId)}
                          data-testid={`conversation-${conversation.userId}`}
                        >
                          <div className="flex items-center space-x-4">
                            <div className="relative">
                              {conversation.user?.profileImageUrl ? (
                                <img
                                  src={conversation.user.profileImageUrl}
                                  alt="User avatar"
                                  className="w-14 h-14 rounded-full object-cover shadow-md"
                                />
                              ) : (
                                <div className="w-14 h-14 rounded-full bg-gradient-to-br from-blue-400 to-purple-500 flex items-center justify-center shadow-md">
                                  <span className="text-white font-bold text-lg">
                                    {conversation.user?.firstName?.[0] || "?"}
                                  </span>
                                </div>
                              )}
                              {/* Online status indicator */}
                              <div className="absolute bottom-0 right-0 w-4 h-4 bg-green-500 rounded-full border-2 border-white shadow-sm"></div>
                            </div>

                            <div className="flex-1 min-w-0">
                              <div className="flex items-start justify-between">
                                <div className="flex-1 min-w-0">
                                  <p
                                    className="font-bold text-gray-900 truncate text-base"
                                    data-testid={`conversation-name-${conversation.userId}`}
                                  >
                                    {conversation.user?.firstName}{" "}
                                    {conversation.user?.lastName}
                                  </p>
                                  <p
                                    className="text-sm text-gray-500 truncate mt-1"
                                    data-testid={`conversation-preview-${conversation.userId}`}
                                  >
                                    {conversation.lastMessage}
                                  </p>
                                </div>

                                <div className="flex flex-col items-end space-y-2 ml-3">
                                  <span className="text-xs text-gray-400 font-medium">
                                    {new Date(
                                      conversation.lastMessageTime
                                    ).toLocaleTimeString([], {
                                      hour: "2-digit",
                                      minute: "2-digit",
                                    })}
                                  </span>
                                  {unreadCount > 0 && (
                                    <div className="bg-red-500 text-white text-xs font-bold px-2 py-1 rounded-full min-w-[22px] h-6 flex items-center justify-center shadow-md">
                                      {unreadCount > 99 ? "99+" : unreadCount}
                                    </div>
                                  )}
                                </div>
                              </div>
                            </div>
                          </div>
                        </div>
                      );
                    }
                  )}
                </div>
              ) : searchQuery ? (
                <div
                  className="p-8 text-center text-gray-500"
                  data-testid="no-search-results"
                >
                  <p>No conversations match "{searchQuery}"</p>
                </div>
              ) : (
                <div
                  className="p-8 text-center text-gray-500"
                  data-testid="empty-conversations"
                >
                  <p>No conversations yet.</p>
                  <p className="text-sm mt-1">
                    Start chatting with product sellers!
                  </p>
                </div>
              )}
            </div>
          </div>

          {/* Chat Area */}
          {selectedUserId ? (
            <ChatArea userId={selectedUserId} />
          ) : (
            <div
              className="flex-1 flex flex-col items-center justify-center text-gray-500 bg-gray-50"
              data-testid="no-chat-selected"
            >
              <div className="text-center">
                <div className="w-24 h-24 bg-white rounded-full flex items-center justify-center mb-6 mx-auto shadow-lg">
                  <FaPlus className="w-10 h-10 text-gray-400" />
                </div>
                <h3 className="text-xl font-semibold text-gray-900 mb-3">
                  No conversation selected
                </h3>
                <p className="text-gray-500">
                  Choose a conversation from the list to start messaging
                </p>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Start Conversation Modal */}
      <StartConversationModal
        isOpen={showStartConversation}
        onClose={() => setShowStartConversation(false)}
        onConversationStarted={(userId) => {
          setSelectedUserId(userId);
        }}
      />
    </div>
  );
}

