import { useState, useEffect, useCallback } from "react";
import { useQuery } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import Sidebar from "@/components/layout/Sidebar";
import ProductCard from "@/components/product/ProductCard";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { Button } from "@/components/ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { FaFire, FaChartLine, FaHashtag, FaExclamationTriangle, FaSync } from "react-icons/fa";
import VroomCard from "@/components/vroom/VroomCard";

interface Product {
  id: string;
  title: string;
  description: string;
  image: string;
  price: number;
  likes: number;
  views: number;
  createdAt: string;
}

interface Vroom {
  id: string;
  name: string;
  description: string;
  coverImageUrl?: string;
  productCount: number;
  _count?: { followers: number; products: number; views: number };
  stats?: { followers: number; products: number; views: number };
}

interface Hashtag {
  tag: string;
  count: string;
}

export default function Trending() {
  const { isAuthenticated, isLoading: authLoading } = useAuth();
  const { toast } = useToast();
  const [shouldRedirect, setShouldRedirect] = useState(false);
  const [activeTab, setActiveTab] = useState("products");

  // Follow state for trending vrooms
  const [followingStates, setFollowingStates] = useState<Record<string, boolean>>({});

  const {
    data: trendingProducts,
    isLoading: productsLoading,
    error: productsError,
    refetch: refetchProducts
  } = useQuery<Product[]>({
    queryKey: ["/api/products/trending"],
    enabled: isAuthenticated,
    retry: false,
  });

  const {
    data: trendingVrooms,
    isLoading: vroomsLoading,
    error: vroomsError,
    refetch: refetchVrooms
  } = useQuery<Vroom[]>({
    queryKey: ["/api/vrooms/trending"],
    enabled: isAuthenticated,
    retry: false,
  });

  const trendingHashtags: Hashtag[] = [
    { tag: "#handmade", count: "2.4K products" },
    { tag: "#vintage", count: "1.8K products" },
    { tag: "#furniture", count: "1.2K products" },
    { tag: "#jewelry", count: "956 products" },
    { tag: "#art", count: "743 products" },
  ];

  useEffect(() => {
    if (!authLoading && !isAuthenticated) {
      toast({
        title: "Authentication required",
        description: "You need to log in to view trending content",
        variant: "destructive",
      });
      setShouldRedirect(true);
    }
  }, [isAuthenticated, authLoading, toast]);

  useEffect(() => {
    if (shouldRedirect) {
      const timer = setTimeout(() => {
        window.location.href = "/api/login";
      }, 1500);
      return () => clearTimeout(timer);
    }
  }, [shouldRedirect]);

  useEffect(() => {
    const errors = [productsError, vroomsError];
    for (const error of errors) {
      if (error && isUnauthorizedError(error as Error)) {
        toast({
          title: "Session expired",
          description: "Please log in again",
          variant: "destructive",
        });
        setShouldRedirect(true);
        return;
      }
    }

    if (productsError && !isUnauthorizedError(productsError as Error)) {
      toast({
        title: "Error loading trending products",
        description: "Please try again later",
        variant: "destructive",
      });
    }
    if (vroomsError && !isUnauthorizedError(vroomsError as Error)) {
      toast({
        title: "Error loading trending vrooms",
        description: "Please try again later",
        variant: "destructive",
      });
    }
  }, [productsError, vroomsError, toast]);

  const handleRetry = useCallback(() => {
    if (productsError) refetchProducts();
    if (vroomsError) refetchVrooms();
  }, [productsError, vroomsError, refetchProducts, refetchVrooms]);

  // Format numbers like 1.2K
  const formatCount = (count: number): string => {
    if (!count) return "0";
    if (count < 1000) return count.toString();
    return (count / 1000).toFixed(1) + "K";
  };

  const handleFollowToggle = async (vroomId: string, currentlyFollowing: boolean) => {
    setFollowingStates(prev => ({ ...prev, [vroomId]: !currentlyFollowing }));

    try {
      const res = await fetch(`/api/vrooms/${vroomId}/follow`, {
        method: currentlyFollowing ? "DELETE" : "POST",
      });
      if (!res.ok) throw new Error("Failed to toggle follow");
    } catch (err) {
      console.error(err);
      setFollowingStates(prev => ({ ...prev, [vroomId]: currentlyFollowing }));
    }
  };

  if (authLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center space-y-4">
          <Skeleton className="h-12 w-12 rounded-full mx-auto" />
          <Skeleton className="h-6 w-48 mx-auto" />
          <Skeleton className="h-4 w-32 mx-auto" />
        </div>
      </div>
    );
  }

  if (!isAuthenticated || shouldRedirect) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-xl font-semibold mb-2">Redirecting to login...</h2>
          <p>Please wait while we redirect you to the login page.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="flex min-h-screen">
      <Sidebar />

      <div className="flex-1 ml-64">
        <div className="max-w-6xl mx-auto p-6">
          {/* Header */}
          <div className="mb-8" data-testid="trending-header">
            <div className="flex items-center justify-between">
              <div>
                <h1 className="text-3xl font-bold flex items-center gap-3 mb-2">
                  <FaFire className="text-accent" />
                  Trending
                </h1>
                <p className="text-muted-foreground">Discover what's popular in the Eldady community</p>
              </div>

              {(productsError || vroomsError) && (
                <Button variant="outline" onClick={handleRetry} className="flex items-center gap-2">
                  <FaSync className="text-muted-foreground" />
                  Retry
                </Button>
              )}
            </div>
          </div>

          {/* Content Tabs */}
          <Tabs value={activeTab} onValueChange={setActiveTab} className="mb-8">
            <TabsList className="grid grid-cols-3 w-full max-w-md mb-6">
              <TabsTrigger value="products" className="flex items-center gap-2"><FaFire /> Products</TabsTrigger>
              <TabsTrigger value="vrooms" className="flex items-center gap-2"><FaChartLine /> Vrooms</TabsTrigger>
              <TabsTrigger value="hashtags" className="flex items-center gap-2"><FaHashtag /> Hashtags</TabsTrigger>
            </TabsList>

            {/* Products Tab */}
            <TabsContent value="products" data-testid="trending-products-section">
              {productsLoading ? (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {[...Array(6)].map((_, i) => <Skeleton key={i} className="h-80 w-full rounded-lg" />)}
                </div>
              ) : trendingProducts && trendingProducts.length > 0 ? (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {trendingProducts.map((product) => <ProductCard key={product.id} product={product} />)}
                </div>
              ) : (
                <div className="text-center py-12 text-muted-foreground">
                  <FaFire className="mx-auto text-4xl mb-4 opacity-50" />
                  <p>No trending products found.</p>
                </div>
              )}
            </TabsContent>

            {/* Vrooms Tab */}
            <TabsContent value="vrooms" data-testid="trending-vrooms-section">
              {vroomsLoading ? (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {[...Array(6)].map((_, i) => <Skeleton key={i} className="h-40 w-full rounded-lg" />)}
                </div>
              ) : trendingVrooms && trendingVrooms.length > 0 ? (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {trendingVrooms.map((vroom) => (
                    <VroomCard
                      key={vroom.id}
                      vroom={{
                        ...vroom,
                        stats: {
                          ...vroom.stats,
                          followers: vroom._count?.followers || vroom.stats?.followers || 0
                        }
                      }}
                      showFollowButton
                      isFollowing={followingStates[vroom.id] || false}
                      onFollowToggle={handleFollowToggle}
                    />
                  ))}
                </div>
              ) : (
                <div className="text-center py-12 text-muted-foreground">
                  <FaChartLine className="mx-auto text-4xl mb-4 opacity-50" />
                  <p>No trending vrooms found.</p>
                </div>
              )}
            </TabsContent>

            {/* Hashtags Tab */}
            <TabsContent value="hashtags" data-testid="trending-hashtags">
              <div className="space-y-3">
                {trendingHashtags.map((item, idx) => (
                  <Badge key={idx} variant="secondary">{item.tag} ({item.count})</Badge>
                ))}
              </div>
            </TabsContent>
          </Tabs>
        </div>
      </div>
    </div>
  );
}

client/src/pages/vroom.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useParams } from "wouter";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import Sidebar from "@/components/layout/Sidebar";
import ProductCard from "@/components/product/ProductCard";
import SelectProductForVroomModal from "@/components/vroom/SelectProductForVroomModal";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Skeleton } from "@/components/ui/skeleton";
import { Badge } from "@/components/ui/badge";
import { 
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { FaUser, FaStore, FaEye, FaHeart, FaPlus, FaEllipsisV, FaEdit, FaTrash, FaShare } from "react-icons/fa";

interface VroomData {
  id: string;
  name: string;
  description?: string;
  coverImageUrl?: string;
  isPublic: boolean;
  userId: string;
  createdAt: string;
  user?: {
    id: string;
    firstName?: string;
    lastName?: string;
    profileImageUrl?: string;
  };
  products?: Array<{
    id: string;
    name: string;
    description?: string;
    price: number;
    imageUrls: string[];
    userId: string;
    createdAt: string;
  }>;
  stats?: {
    followers: number;
    views: number;
    isFollowing: boolean;
  };
}

export default function Vroom() {
  const { id } = useParams();
  const { user, isAuthenticated, isLoading: authLoading } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [showAddProduct, setShowAddProduct] = useState(false);

  const {
    data: vroom,
    isLoading,
    error
  } = useQuery<VroomData>({
    queryKey: ["/api/vrooms", id],
    enabled: !!id,
    retry: false,
  });

  // Follow/Unfollow mutation
  const followMutation = useMutation({
    mutationFn: async () => {
      const method = vroom?.stats?.isFollowing ? "DELETE" : "POST";
      return await apiRequest(method, `/api/vrooms/${id}/follow`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/vrooms", id] });
      toast({
        title: "Success",
        description: vroom?.stats?.isFollowing ? "Unfollowed vroom" : "Following vroom",
      });
    },
    onError: (error: any) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: error?.message || "Failed to update follow status",
        variant: "destructive",
      });
    },
  });

  // Delete vroom mutation
  const deleteMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest("DELETE", `/api/vrooms/${id}`);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Vroom deleted successfully",
      });
      window.location.href = "/profile";
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error?.message || "Failed to delete vroom",
        variant: "destructive",
      });
    },
  });

  const handleShare = async () => {
    try {
      await navigator.share({
        title: vroom?.name,
        text: vroom?.description,
        url: window.location.href,
      });
    } catch {
      await navigator.clipboard.writeText(window.location.href);
      toast({
        title: "Success",
        description: "Link copied to clipboard!",
      });
    }
  };

  const handleDelete = () => {
    if (window.confirm("Are you sure you want to delete this vroom? This action cannot be undone.")) {
      deleteMutation.mutate();
    }
  };

  if (authLoading || isLoading) {
    return (
      <div className="flex min-h-screen">
        <Sidebar />
        <div className="flex-1 ml-64 p-6">
          <Skeleton className="h-40 w-full mb-6" />
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {[...Array(6)].map((_, i) => (
              <Skeleton key={i} className="h-80 w-full" />
            ))}
          </div>
        </div>
      </div>
    );
  }

  if (error && isUnauthorizedError(error as Error)) {
    toast({
      title: "Unauthorized",
      description: "You are logged out. Logging in again...",
      variant: "destructive",
    });
    setTimeout(() => {
      window.location.href = "/api/login";
    }, 500);
    return null;
  }

  if (!vroom) {
    return (
      <div className="flex min-h-screen">
        <Sidebar />
        <div className="flex-1 ml-64 p-6">
          <div className="text-center py-12" data-testid="vroom-not-found">
            <FaStore className="mx-auto text-6xl text-muted-foreground mb-4" />
            <h2 className="text-2xl font-semibold mb-2">Vroom not found</h2>
            <p className="text-muted-foreground">This vroom doesn't exist or has been removed.</p>
          </div>
        </div>
      </div>
    );
  }

  const isOwner = user && vroom.userId === user.id;
  const canEdit = isOwner;
  const productCount = vroom.products?.length || 0;
  const followerCount = vroom.stats?.followers || 0;

  return (
    <div className="flex min-h-screen">
      <Sidebar />

      <div className="flex-1 ml-64">
        <div className="max-w-6xl mx-auto p-6">
          {/* Vroom Header */}
          <Card className="mb-6" data-testid="vroom-header">
            <CardContent className="p-6">
              {/* Cover Image */}
              {vroom.coverImageUrl && (
                <div className="w-full h-48 mb-6 rounded-xl overflow-hidden">
                  <img
                    src={vroom.coverImageUrl}
                    alt={vroom.name}
                    className="w-full h-full object-cover"
                    data-testid="vroom-cover-image"
                  />
                </div>
              )}

              <div className="flex items-start space-x-4">
                {/* Vroom Icon */}
                <div className="w-20 h-20 rounded-xl bg-gradient-to-br from-primary/20 to-accent/20 flex items-center justify-center flex-shrink-0">
                  <FaStore className="text-2xl text-primary" />
                </div>

                <div className="flex-1 min-w-0">
                  <div className="flex items-start justify-between mb-2">
                    <h1 className="text-3xl font-bold" data-testid="vroom-name">
                      {vroom.name}
                    </h1>

                    <div className="flex items-center gap-2">
                      <Badge variant={vroom.isPublic ? "default" : "secondary"}>
                        {vroom.isPublic ? "Public" : "Private"}
                      </Badge>

                      {canEdit && (
                        <DropdownMenu>
                          <DropdownMenuTrigger asChild>
                            <Button variant="outline" size="sm" data-testid="button-vroom-menu">
                              <FaEllipsisV />
                            </Button>
                          </DropdownMenuTrigger>
                          <DropdownMenuContent align="end">
                            <DropdownMenuItem data-testid="menu-edit-vroom">
                              <FaEdit className="mr-2" />
                              Edit Vroom
                            </DropdownMenuItem>
                            <DropdownMenuItem 
                              onClick={handleDelete}
                              className="text-destructive"
                              data-testid="menu-delete-vroom"
                            >
                              <FaTrash className="mr-2" />
                              Delete Vroom
                            </DropdownMenuItem>
                          </DropdownMenuContent>
                        </DropdownMenu>
                      )}
                    </div>
                  </div>

                  {vroom.description && (
                    <p className="text-muted-foreground mb-4" data-testid="vroom-description">
                      {vroom.description}
                    </p>
                  )}

                  {/* Owner Info */}
                  {vroom.user && (
                    <div className="flex items-center gap-2 mb-4">
                      {vroom.user.profileImageUrl ? (
                        <img
                          src={vroom.user.profileImageUrl}
                          alt="Owner"
                          className="w-8 h-8 rounded-full"
                        />
                      ) : (
                        <div className="w-8 h-8 rounded-full bg-muted flex items-center justify-center">
                          <FaUser className="text-xs" />
                        </div>
                      )}
                      <span className="font-medium">
                        {vroom.user.firstName} {vroom.user.lastName}
                      </span>
                    </div>
                  )}

                  {/* Stats */}
                  <div className="flex items-center space-x-6 text-sm text-muted-foreground mb-4">
                    <span data-testid="vroom-product-count">
                      <strong>{productCount}</strong> products
                    </span>
                    <span data-testid="vroom-followers">
                      <strong>{followerCount}</strong> followers
                    </span>
                  </div>

                  {/* Action Buttons */}
                  <div className="flex items-center gap-3">
                    {!isOwner && isAuthenticated && (
                      <Button 
                        onClick={() => followMutation.mutate()}
                        disabled={followMutation.isPending}
                        variant={vroom.stats?.isFollowing ? "outline" : "default"}
                        data-testid="button-follow-vroom"
                      >
                        <FaHeart className={`mr-2 ${vroom.stats?.isFollowing ? 'text-red-500' : ''}`} />
                        {vroom.stats?.isFollowing ? "Following" : "Follow"}
                      </Button>
                    )}

                    {isOwner && (
                      <Button 
                        onClick={() => setShowAddProduct(true)}
                        data-testid="button-add-product-to-vroom"
                      >
                        <FaPlus className="mr-2" />
                        Add Product
                      </Button>
                    )}

                    <Button 
                      variant="outline" 
                      onClick={handleShare}
                      data-testid="button-share-vroom"
                    >
                      <FaShare className="mr-2" />
                      Share
                    </Button>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Products Section */}
          <div className="mb-6">
            <h2 className="text-xl font-semibold mb-4">Products ({productCount})</h2>

            {vroom.products && vroom.products.length > 0 ? (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6" data-testid="vroom-products-grid">
                {vroom.products.map((product) => (
                  <ProductCard 
                    key={product.id} 
                    product={{
                      ...product,
                      description: product.description || "",
                      price: product.price.toString(),
                      imageUrls: product.imageUrls || [],
                      likes: 0,
                      userId: product.userId,
                      user: vroom.user
                    }}
                    showAddToVroom={false}
                  />
                ))}
              </div>
            ) : (
              <Card>
                <CardContent className="p-12 text-center" data-testid="empty-vroom-products">
                  <FaStore className="mx-auto text-6xl text-muted-foreground mb-4" />
                  <h3 className="text-lg font-medium mb-2">No products yet</h3>
                  <p className="text-muted-foreground mb-4">
                    {isOwner 
                      ? "Start building your collection by adding products to this vroom."
                      : "This vroom doesn't have any products yet."
                    }
                  </p>
                  {isOwner && (
                    <Button onClick={() => setShowAddProduct(true)}>
                      <FaPlus className="mr-2" />
                      Add Your First Product
                    </Button>
                  )}
                </CardContent>
              </Card>
            )}
          </div>
        </div>
      </div>

      {/* Add Product to Vroom Modal */}
      {showAddProduct && (
        <SelectProductForVroomModal
          isOpen={showAddProduct}
          onClose={() => setShowAddProduct(false)}
          vroomId={id || ""}
        />
      )}
    </div>
  );
}

client/src/components/cart/CheckoutModal.content:
import { useState, useEffect } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { apiRequest } from "@/lib/queryClient";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { FaTimes, FaTruck, FaExclamationTriangle } from "react-icons/fa";

interface CheckoutModalProps {
  isOpen: boolean;
  onClose: () => void;
  cartItems: any[];
}

interface ShippingAddress {
  country: string;
  city: string;
  streetAddress: string;
}

export default function CheckoutModal({ isOpen, onClose, cartItems }: CheckoutModalProps) {
  const [shippingAddress, setShippingAddress] = useState<ShippingAddress>({
    country: "",
    city: "",
    streetAddress: "",
  });
  const [hasOwnProducts, setHasOwnProducts] = useState(false);
  const [filteredCartItems, setFilteredCartItems] = useState<any[]>([]);

  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Filter out user's own products and check if any were removed
  useEffect(() => {
    if (user && cartItems) {
      const filteredItems = cartItems.filter(item => 
        item.product && item.product.userId !== (user as any)?.id
      );

      setFilteredCartItems(filteredItems);
      setHasOwnProducts(filteredItems.length !== cartItems.length);
    }
  }, [cartItems, user]);

  const createOrderMutation = useMutation({
    mutationFn: async (orderData: any) => {
      const response = await apiRequest("POST", "/api/orders", orderData);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Order Placed!",
        description: "Your order has been placed successfully. The seller will contact you soon.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/cart"] });
      queryClient.invalidateQueries({ queryKey: ["/api/orders"] });
      handleClose();
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to place order. Please try again.",
        variant: "destructive",
      });
    },
  });

  const sendMessageMutation = useMutation({
    mutationFn: async (messageData: any) => {
      const response = await apiRequest("POST", "/api/messages", messageData);
      return response.json();
    },
    onError: (error) => {
      console.error("Failed to send message:", error);
    },
  });

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!shippingAddress.country || !shippingAddress.city || !shippingAddress.streetAddress) {
      toast({
        title: "Error",
        description: "Please fill in all shipping address fields",
        variant: "destructive",
      });
      return;
    }

    if (filteredCartItems.length === 0) {
      toast({
        title: "No items to purchase",
        description: "You cannot purchase your own products.",
        variant: "destructive",
      });
      return;
    }

    try {
      // Group items by seller
      const itemsBySeller: { [sellerId: string]: any[] } = {};
      filteredCartItems.forEach(item => {
        if (item.product && item.product.userId) {
          const sellerId = item.product.userId;
          if (!itemsBySeller[sellerId]) {
            itemsBySeller[sellerId] = [];
          }
          itemsBySeller[sellerId].push(item);
        }
      });

      // Create separate orders for each product and send PRIVATE messages
      for (const [sellerId, sellerItems] of Object.entries(itemsBySeller)) {
        for (const item of sellerItems) {
          if (item.product) {
            // Create order
            const orderResponse = await createOrderMutation.mutateAsync({
              sellerId,
              productId: item.productId,
              quantity: item.quantity,
              totalAmount: (parseFloat(item.product.price) * item.quantity).toString(),
              shippingAddress,
            });

            // Send PRIVATE order notification to seller (only visible to seller)
            const sellerMessage = `
New Order Received!

Customer: ${user?.name || user?.email}
Shipping Address:
${shippingAddress.streetAddress}
${shippingAddress.city}, ${shippingAddress.country}

Product: ${item.product.name} Ã— ${item.quantity}
Price: $${item.product.price} each
Total: $${(parseFloat(item.product.price) * item.quantity).toFixed(2)}

Order ID: ${orderResponse.id}
Please contact the customer to arrange delivery and payment.
            `.trim();

            await sendMessageMutation.mutateAsync({
              senderId: user?.id, // Buyer sends this notification
              receiverId: sellerId,
              content: sellerMessage,
              messageType: 'order_notification', // This type is only for sellers
              orderId: orderResponse.id,
              metadata: {
                orderId: orderResponse.id,
                productName: item.product.name,
                quantity: item.quantity,
                totalAmount: (parseFloat(item.product.price) * item.quantity).toFixed(2)
              }
            });
          }
        }
      }

      // Send PRIVATE order confirmation to buyer (only visible to buyer)
      const buyerMessage = `
Order Confirmation!

Thank you for your order with ELDADY MART.

Order Summary:
${filteredCartItems.map(item => `
- ${item.product.name} Ã— ${item.quantity}
  Price: $${item.product.price} each
  Total: $${(parseFloat(item.product.price) * item.quantity).toFixed(2)}
`).join('')}

Grand Total: $${calculateTotal()}

Shipping Address:
${shippingAddress.streetAddress}
${shippingAddress.city}, ${shippingAddress.country}

Your sellers will contact you shortly to arrange delivery and payment.
Thank you for shopping with ELDADY MART!
      `.trim();

      // Use a system sender or the user themselves for the confirmation
      await sendMessageMutation.mutateAsync({
        senderId: user?.id, // Or use a system user ID if available
        receiverId: user?.id,
        content: buyerMessage,
        messageType: 'order_confirmation', // This type is only for buyers
        metadata: {
          orderCount: filteredCartItems.length,
          grandTotal: calculateTotal(),
          shippingAddress: shippingAddress
        }
      });

      toast({
        title: "Order Placed!",
        description: "Your order has been placed successfully. Check your messages for details.",
      });

      queryClient.invalidateQueries({ queryKey: ["/api/cart"] });
      queryClient.invalidateQueries({ queryKey: ["/api/orders"] });
      queryClient.invalidateQueries({ queryKey: ["/api/messages/orders/buyer"] });
      queryClient.invalidateQueries({ queryKey: ["/api/messages/orders/seller"] });
      handleClose();

    } catch (error) {
      console.error("Checkout error:", error);
      toast({
        title: "Error",
        description: "Failed to place order. Please try again.",
        variant: "destructive",
      });
    }
  };

  const handleClose = () => {
    setShippingAddress({
      country: "",
      city: "",
      streetAddress: "",
    });
    setHasOwnProducts(false);
    onClose();
  };

  const calculateTotal = () => {
    return filteredCartItems.reduce((total, item) => {
      return total + (parseFloat(item.product?.price || 0) * item.quantity);
    }, 0).toFixed(2);
  };

  const countries = [
    "United States",
    "Canada",
    "United Kingdom",
    "Germany",
    "France",
    "Australia",
    "Japan",
    "Nigeria",
    "Ghana",
    "Kenya",
    "South Africa",
    "Egypt",
    "Other"
  ];

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="max-w-md max-h-[90vh] overflow-y-auto" data-testid="checkout-modal">
        <DialogHeader>
          <div className="flex items-center justify-between">
            <DialogTitle data-testid="checkout-title">Checkout Details</DialogTitle>
            <Button
              variant="ghost"
              size="sm"
              onClick={handleClose}
              data-testid="button-close-checkout"
              className="h-8 w-8 p-0 rounded-full"
            >
              <FaTimes className="h-4 w-4" />
            </Button>
          </div>
        </DialogHeader>

        {hasOwnProducts && (
          <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-3 flex items-start gap-3">
            <FaExclamationTriangle className="text-yellow-600 mt-0.5 flex-shrink-0" />
            <div className="text-sm text-yellow-800">
              <p className="font-medium">Notice</p>
              <p>Your own products have been removed from the order. You cannot purchase items that you are selling.</p>
            </div>
          </div>
        )}

        {filteredCartItems.length === 0 ? (
          <div className="text-center py-8">
            <FaExclamationTriangle className="h-12 w-12 text-muted-foreground/50 mx-auto mb-4" />
            <h3 className="font-medium text-lg mb-2">No items to purchase</h3>
            <p className="text-muted-foreground mb-4">
              You cannot purchase your own products. Please add items from other sellers to your cart.
            </p>
            <Button onClick={handleClose} variant="outline">
              Continue Shopping
            </Button>
          </div>
        ) : (
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <Label htmlFor="country">Country</Label>
              <Select
                value={shippingAddress.country}
                onValueChange={(value) => setShippingAddress(prev => ({ ...prev, country: value }))}
              >
                <SelectTrigger data-testid="select-country">
                  <SelectValue placeholder="Select Country" />
                </SelectTrigger>
                <SelectContent>
                  {countries.map(country => (
                    <SelectItem key={country} value={country}>
                      {country}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label htmlFor="city">Town/City</Label>
              <Input
                id="city"
                value={shippingAddress.city}
                onChange={(e) => setShippingAddress(prev => ({ ...prev, city: e.target.value }))}
                placeholder="Enter your town or city"
                required
                data-testid="input-city"
              />
            </div>

            <div>
              <Label htmlFor="streetAddress">Street Address</Label>
              <Textarea
                id="streetAddress"
                value={shippingAddress.streetAddress}
                onChange={(e) => setShippingAddress(prev => ({ ...prev, streetAddress: e.target.value }))}
                placeholder="Enter your street address"
                className="h-20 resize-none"
                required
                data-testid="textarea-street-address"
              />
            </div>

            <div className="bg-accent/10 p-4 rounded-lg" data-testid="payment-method-info">
              <div className="flex items-center space-x-2 mb-2">
                <FaTruck className="text-accent" />
                <span className="font-medium">Payment Method</span>
              </div>
              <p className="text-sm text-muted-foreground">
                Pay on Delivery - Cash payment when your order arrives
              </p>
            </div>

            {/* Order Summary */}
            <div className="bg-muted/30 p-4 rounded-lg" data-testid="order-summary">
              <h3 className="font-medium mb-3">Order Summary</h3>
              <div className="space-y-2">
                {filteredCartItems.map((item) => (
                  <div key={item.id} className="flex justify-between text-sm">
                    <span>{item.product?.name} Ã— {item.quantity}</span>
                    <span>${(parseFloat(item.product?.price || 0) * item.quantity).toFixed(2)}</span>
                  </div>
                ))}
                <div className="border-t border-border pt-2 mt-2">
                  <div className="flex justify-between font-semibold">
                    <span>Total</span>
                    <span data-testid="checkout-total">${calculateTotal()}</span>
                  </div>
                </div>
              </div>
            </div>

            <div className="flex space-x-3 pt-4">
              <Button
                type="button"
                variant="outline"
                onClick={handleClose}
                className="flex-1"
                data-testid="button-cancel-checkout"
              >
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={createOrderMutation.isPending || sendMessageMutation.isPending}
                className="flex-1 bg-primary text-primary-foreground hover:bg-primary/90"
                data-testid="button-place-order"
              >
                {(createOrderMutation.isPending || sendMessageMutation.isPending) 
                  ? "Processing..." 
                  : "Place Order"}
              </Button>
            </div>
          </form>
        )}
      </DialogContent>
    </Dialog>
  );
}

