import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { apiRequest } from "@/lib/queryClient";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Skeleton } from "@/components/ui/skeleton";
import { FaStore, FaPlus, FaCheck } from "react-icons/fa";

interface AddProductToVroomModalProps {
  isOpen: boolean;
  onClose: () => void;
  productId: string;
  productName: string;
}

export default function AddProductToVroomModal({ 
  isOpen, 
  onClose, 
  productId, 
  productName 
}: AddProductToVroomModalProps) {
  const { toast } = useToast();
  const { user } = useAuth();
  const queryClient = useQueryClient();
  const [selectedVrooms, setSelectedVrooms] = useState<string[]>([]);

  // Fetch user's vrooms
  const { data: userVrooms, isLoading } = useQuery({
    queryKey: ["/api/vrooms/user"],
    enabled: isOpen && !!user,
    retry: false,
  });

  // Fetch current product's vrooms
  const { data: productVrooms } = useQuery({
    queryKey: ["/api/products", productId, "vrooms"],
    enabled: isOpen && !!productId,
    retry: false,
  });

  const addToVroomMutation = useMutation({
    mutationFn: async ({ vroomId, productId }: { vroomId: string; productId: string }) => {
      return await apiRequest("POST", `/api/vrooms/${vroomId}/products`, { productId });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Product added to vroom successfully!",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/vrooms"] });
      queryClient.invalidateQueries({ queryKey: ["/api/products", productId, "vrooms"] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error?.message || "Failed to add product to vroom",
        variant: "destructive",
      });
    },
  });

  const removeFromVroomMutation = useMutation({
    mutationFn: async ({ vroomId, productId }: { vroomId: string; productId: string }) => {
      return await apiRequest("DELETE", `/api/vrooms/${vroomId}/products/${productId}`);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Product removed from vroom successfully!",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/vrooms"] });
      queryClient.invalidateQueries({ queryKey: ["/api/products", productId, "vrooms"] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error?.message || "Failed to remove product from vroom",
        variant: "destructive",
      });
    },
  });

  const handleVroomToggle = (vroomId: string) => {
    const currentProductVrooms = Array.isArray(productVrooms) ? productVrooms.map((v: any) => v.id) : [];
    const isCurrentlyInVroom = currentProductVrooms.includes(vroomId);

    if (isCurrentlyInVroom) {
      removeFromVroomMutation.mutate({ vroomId, productId });
    } else {
      addToVroomMutation.mutate({ vroomId, productId });
    }
  };

  const isProductInVroom = (vroomId: string) => {
    if (!Array.isArray(productVrooms)) return false;
    return productVrooms.some((v: any) => v.id === vroomId);
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-lg max-h-[80vh] overflow-y-auto" data-testid="add-product-to-vroom-modal">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <FaStore className="text-primary" />
            Add "{productName}" to Vroom
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          {isLoading ? (
            <div className="space-y-3">
              {[...Array(3)].map((_, i) => (
                <Skeleton key={i} className="h-16 w-full" />
              ))}
            </div>
          ) : userVrooms && Array.isArray(userVrooms) && userVrooms.length > 0 ? (
            <div className="space-y-3">
              {userVrooms.map((vroom: any) => {
                const isInVroom = isProductInVroom(vroom.id);
                const isProcessing = addToVroomMutation.isPending || removeFromVroomMutation.isPending;

                return (
                  <Card
                    key={vroom.id}
                    className={`cursor-pointer transition-all ${
                      isInVroom ? 'ring-2 ring-primary' : 'hover:shadow-md'
                    }`}
                    onClick={() => !isProcessing && handleVroomToggle(vroom.id)}
                    data-testid={`vroom-option-${vroom.id}`}
                  >
                    <CardContent className="p-4">
                      <div className="flex items-center gap-3">
                        {vroom.coverImageUrl ? (
                          <img
                            src={vroom.coverImageUrl}
                            alt={vroom.name}
                            className="w-12 h-12 rounded-lg object-cover"
                          />
                        ) : (
                          <div className="w-12 h-12 rounded-lg bg-muted flex items-center justify-center">
                            <FaStore className="text-muted-foreground" />
                          </div>
                        )}

                        <div className="flex-1">
                          <h4 className="font-medium">{vroom.name}</h4>
                          {vroom.description && (
                            <p className="text-sm text-muted-foreground line-clamp-1">
                              {vroom.description}
                            </p>
                          )}
                        </div>

                        <div className="flex items-center">
                          {isInVroom ? (
                            <div className="w-6 h-6 rounded-full bg-primary flex items-center justify-center">
                              <FaCheck className="text-primary-foreground text-xs" />
                            </div>
                          ) : (
                            <div className="w-6 h-6 rounded-full border-2 border-muted-foreground/30" />
                          )}
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                );
              })}
            </div>
          ) : (
            <div className="text-center py-8 text-muted-foreground">
              <FaStore className="mx-auto text-4xl mb-4 opacity-50" />
              <p>You don't have any vrooms yet.</p>
              <p className="text-sm">Create a vroom first to organize your products!</p>
            </div>
          )}

          <div className="flex gap-3 pt-4">
            <Button 
              variant="outline" 
              onClick={onClose}
              className="flex-1"
              data-testid="button-close-add-to-vroom"
            >
              Done
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/vroom/CreateVroomM.content:
import { useState, useRef } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { ObjectUploader } from "@/components/ObjectUploader";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { FaPlus, FaStore, FaCamera, FaLink, FaTimes } from "react-icons/fa";
import type { UploadResult } from "@uppy/core";

interface CreateVroomModalProps {
  trigger?: React.ReactNode;
  isOpen?: boolean;
  onClose?: () => void;
}

export default function CreateVroomModal({ trigger, isOpen, onClose }: CreateVroomModalProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [modalOpen, setModalOpen] = useState(false);
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    isPublic: true,
  });
  const [coverImageUrl, setCoverImageUrl] = useState<string>("");
  const [imageUrlInput, setImageUrlInput] = useState("");
  const [activeTab, setActiveTab] = useState("upload");
  const fileInputRef = useRef<HTMLInputElement>(null);

  const createVroomMutation = useMutation({
    mutationFn: async (data: any) => {
      return await apiRequest("POST", "/api/vrooms", data);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Vroom created successfully!",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/vrooms"] });
      queryClient.invalidateQueries({ queryKey: ["/api/vrooms/user"] });
      handleClose();
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error?.message || "Failed to create vroom",
        variant: "destructive",
      });
    },
  });

  const handleGetUploadParameters = async () => {
    const response = await apiRequest('POST', '/api/objects/upload') as { uploadURL: string };
    return {
      method: 'PUT' as const,
      url: response.uploadURL,
    };
  };

  const handleImageUpload = async (result: UploadResult<Record<string, unknown>, Record<string, unknown>>) => {
    if (result.successful && result.successful.length > 0) {
      const uploadedFile = result.successful[0];
      try {
        const response = await apiRequest("PUT", "/api/vroom-images", {
          imageURL: uploadedFile.uploadURL,
        });
        const data = await response.json();
        setCoverImageUrl(data.objectPath);

        toast({
          title: "Success",
          description: "Cover image uploaded successfully!",
        });
      } catch (error) {
        toast({
          title: "Error",
          description: "Failed to process uploaded image",
          variant: "destructive",
        });
      }
    }
  };

  const handleAddUrlImage = () => {
    if (!imageUrlInput) {
      toast({
        title: "Error",
        description: "Please enter an image URL",
        variant: "destructive",
      });
      return;
    }

    try {
      // Basic URL validation
      new URL(imageUrlInput);
      setCoverImageUrl(imageUrlInput);
      setImageUrlInput("");

      toast({
        title: "Success",
        description: "Image URL added successfully!",
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Please enter a valid URL",
        variant: "destructive",
      });
    }
  };

  const handleFileUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = e.target.files;
    if (!files || files.length === 0) return;

    const file = files[0];
    if (file.size > 5 * 1024 * 1024) { // 5MB limit
      toast({
        title: "Error",
        description: "File is too large. Maximum size is 5MB.",
        variant: "destructive",
      });
      return;
    }

    const reader = new FileReader();
    reader.onload = (event) => {
      const imageUrl = event.target?.result as string;
      setCoverImageUrl(imageUrl);

      toast({
        title: "Success",
        description: "Image uploaded successfully!",
      });
    };
    reader.readAsDataURL(file);

    // Reset the file input
    if (fileInputRef.current) {
      fileInputRef.current.value = "";
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.name.trim()) {
      toast({
        title: "Error",
        description: "Vroom name is required",
        variant: "destructive",
      });
      return;
    }

    createVroomMutation.mutate({
      ...formData,
      coverImageUrl: coverImageUrl || null,
    });
  };

  const handleClose = () => {
    setFormData({ name: "", description: "", isPublic: true });
    setCoverImageUrl("");
    setImageUrlInput("");
    setActiveTab("upload");
    if (onClose) {
      onClose();
    } else {
      setModalOpen(false);
    }
  };

  const open = isOpen !== undefined ? isOpen : modalOpen;
  const setOpen = isOpen !== undefined ? (onClose || (() => {})) : setModalOpen;

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      {trigger && (
        <DialogTrigger asChild>
          {trigger}
        </DialogTrigger>
      )}
      {!trigger && (
        <DialogTrigger asChild>
          <Button className="flex items-center gap-2" data-testid="button-create-vroom">
            <FaPlus />
            Create Vroom
          </Button>
        </DialogTrigger>
      )}

      <DialogContent className="max-w-lg max-h-[90vh] overflow-y-auto" data-testid="create-vroom-modal">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <FaStore className="text-primary" />
            Create New Vroom
          </DialogTitle>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <Label htmlFor="name">Vroom Name</Label>
            <Input
              id="name"
              value={formData.name}
              onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
              placeholder="Enter vroom name..."
              required
              data-testid="input-vroom-name"
            />
          </div>

          <div>
            <Label htmlFor="description">Description</Label>
            <Textarea
              id="description"
              value={formData.description}
              onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
              placeholder="Describe your vroom..."
              rows={3}
              data-testid="input-vroom-description"
            />
          </div>

          <div className="space-y-2">
            <Label>Cover Image</Label>

            <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
              <TabsList className="grid grid-cols-2 mb-3">
                <TabsTrigger value="upload" className="flex items-center gap-2">
                  <FaCamera className="h-3 w-3" />
                  Upload
                </TabsTrigger>
                <TabsTrigger value="url" className="flex items-center gap-2">
                  <FaLink className="h-3 w-3" />
                  URL
                </TabsTrigger>
              </TabsList>

              <TabsContent value="upload" className="space-y-3">
                <div 
                  className="border-2 border-dashed border-gray-300 rounded-lg p-4 text-center cursor-pointer hover:border-primary transition-colors"
                  onClick={() => fileInputRef.current?.click()}
                >
                  <FaCamera className="h-6 w-6 mx-auto text-gray-400 mb-2" />
                  <p className="text-sm font-medium">Click to upload or drag and drop</p>
                  <p className="text-xs text-muted-foreground mt-1">PNG, JPG up to 5MB</p>
                  <Input
                    ref={fileInputRef}
                    type="file"
                    accept="image/*"
                    onChange={handleFileUpload}
                    className="hidden"
                  />
                </div>
              </TabsContent>

              <TabsContent value="url" className="space-y-3">
                <div className="flex gap-2">
                  <Input
                    value={imageUrlInput}
                    onChange={(e) => setImageUrlInput(e.target.value)}
                    placeholder="Paste image URL here"
                    className="flex-1"
                  />
                  <Button 
                    type="button" 
                    onClick={handleAddUrlImage}
                    disabled={!imageUrlInput}
                  >
                    Add Image
                  </Button>
                </div>
              </TabsContent>
            </Tabs>

            {coverImageUrl && (
              <div className="mt-3 relative">
                <img
                  src={coverImageUrl}
                  alt="Cover preview"
                  className="w-full h-32 object-cover rounded-lg"
                />
                <Button
                  type="button"
                  variant="destructive"
                  size="icon"
                  className="absolute -top-2 -right-2 h-6 w-6 rounded-full"
                  onClick={() => setCoverImageUrl("")}
                >
                  <FaTimes className="h-3 w-3" />
                </Button>
              </div>
            )}
          </div>

          <div className="flex items-center space-x-2">
            <Switch
              id="public"
              checked={formData.isPublic}
              onCheckedChange={(checked) => setFormData(prev => ({ ...prev, isPublic: checked }))}
              data-testid="switch-vroom-public"
            />
            <Label htmlFor="public">Make this vroom public</Label>
          </div>

          <div className="flex gap-3 pt-4">
            <Button 
              type="submit" 
              disabled={createVroomMutation.isPending}
              className="flex-1"
              data-testid="button-submit-vroom"
            >
              {createVroomMutation.isPending ? "Creating..." : "Create Vroom"}
            </Button>
            <Button 
              type="button" 
              variant="outline" 
              onClick={handleClose}
              data-testid="button-cancel-vroom"
            >
              Cancel
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

