import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { useLocation } from "wouter";
import { FaComments, FaPaperPlane, FaTimes } from "react-icons/fa";

interface MessageSellerButtonProps {
  sellerId: string;
  sellerName?: string;
  productName?: string;
  variant?: "default" | "outline" | "secondary" | "ghost" | "link" | "destructive";
  size?: "default" | "sm" | "lg" | "icon";
  className?: string;
}

export default function MessageSellerButton({ 
  sellerId, 
  sellerName = "the seller",
  productName = "your product",
  variant = "outline",
  size = "sm",
  className = ""
}: MessageSellerButtonProps) {
  const { user } = useAuth();
  const { toast } = useToast();
  const [, setLocation] = useLocation();
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [messageContent, setMessageContent] = useState("");

  const startConversationMutation = useMutation({
    mutationFn: async (customMessage?: string) => {
      const message = customMessage || `Hi! I'm interested in ${productName}.`;

      const response = await apiRequest("POST", "/api/messages/start", {
        receiverId: sellerId,
        content: message,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to start conversation");
      }

      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Message sent!",
        description: `Your message has been sent to ${sellerName}.`,
      });

      setIsDialogOpen(false);
      setMessageContent("");

      // Navigate to the specific conversation
      setLocation(`/messages/${data.conversationId}`);
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to send message. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSendMessage = () => {
    const finalMessage = messageContent.trim() || `Hi! I'm interested in ${productName}.`;
    startConversationMutation.mutate(finalMessage);
  };

  const handleOpenDialog = () => {
    setMessageContent(`Hi! I'm interested in ${productName}.`);
    setIsDialogOpen(true);
  };

  const handleCloseDialog = () => {
    setIsDialogOpen(false);
    setMessageContent("");
  };

  // Don't show button if trying to message yourself or if user is not logged in
  if ((user as any)?.id === sellerId || !user) {
    return null;
  }

  return (
    <>
      <Button
        onClick={handleOpenDialog}
        disabled={startConversationMutation.isPending}
        variant={variant}
        size={size}
        className={`flex items-center gap-2 ${className}`}
        data-testid="button-message-seller"
      >
        <FaComments className="h-4 w-4" />
        {startConversationMutation.isPending ? "Sending..." : "Message Seller"}
      </Button>

      <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>Message {sellerName}</DialogTitle>
            <DialogDescription>
              Send a message to inquire about {productName}.
            </DialogDescription>
          </DialogHeader>

          <div className="space-y-4">
            <Textarea
              value={messageContent}
              onChange={(e) => setMessageContent(e.target.value)}
              placeholder={`Type your message to ${sellerName}...`}
              className="min-h-32 resize-none"
              disabled={startConversationMutation.isPending}
            />

            <div className="text-sm text-muted-foreground">
              Tip: Be specific about what you're interested in to get a faster response.
            </div>
          </div>

          <DialogFooter className="flex flex-col sm:flex-row gap-2">
            <Button
              type="button"
              variant="outline"
              onClick={handleCloseDialog}
              disabled={startConversationMutation.isPending}
              className="flex-1 sm:flex-initial"
            >
              <FaTimes className="h-4 w-4 mr-2" />
              Cancel
            </Button>
            <Button
              type="button"
              onClick={handleSendMessage}
              disabled={startConversationMutation.isPending || !messageContent.trim()}
              className="flex-1 sm:flex-initial"
            >
              <FaPaperPlane className="h-4 w-4 mr-2" />
              {startConversationMutation.isPending ? "Sending..." : "Send Message"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </>
  );
}

client/src/hooks/useUnreadMessages.ts.content:
import { useQuery } from "@tanstack/react-query";
import { useEffect, useState } from "react";
import { queryClient } from "@/lib/queryClient";
import { useAuth } from "./useAuth";

export function useUnreadMessages() {
  const { isAuthenticated, user } = useAuth();
  const [socket, setSocket] = useState<WebSocket | null>(null);

  // Query for unread count
  const { data: unreadCount = 0, isLoading } = useQuery({
    queryKey: ["/api/messages/unread-count"],
    enabled: isAuthenticated,
    retry: false,
    refetchInterval: 30000, // Refetch every 30 seconds as fallback
  });

  // WebSocket for real-time updates
  useEffect(() => {
    if (!isAuthenticated || !user) return;

    const protocol = window.location.protocol === "https:" ? "wss:" : "ws:";
    const wsUrl = `${protocol}//${window.location.host}/ws`;
    const ws = new WebSocket(wsUrl);

    ws.onopen = () => {
      console.log("Unread messages WebSocket connected");
      setSocket(ws);
    };

    ws.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);
        if (data.type === "message") {
          // Invalidate unread count when new message arrives
          queryClient.invalidateQueries({ queryKey: ["/api/messages/unread-count"] });
        }
      } catch (error) {
        console.error("Error parsing WebSocket message:", error);
      }
    };

    ws.onclose = () => {
      console.log("Unread messages WebSocket disconnected");
      setSocket(null);
    };

    ws.onerror = (error) => {
      console.error("Unread messages WebSocket error:", error);
    };

    return () => {
      ws.close();
    };
  }, [isAuthenticated, user]);

  return {
    unreadCount: typeof unreadCount === 'number' ? unreadCount : 0,
    isLoading,
    socket
  };
}

client/src/components/vroom/SelectProduc.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { apiRequest } from "@/lib/queryClient";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Skeleton } from "@/components/ui/skeleton";
import { Badge } from "@/components/ui/badge";
import { FaStore, FaPlus, FaCheck } from "react-icons/fa";

interface SelectProductForVroomModalProps {
  isOpen: boolean;
  onClose: () => void;
  vroomId: string;
}

interface Product {
  id: string;
  name: string;
  description?: string;
  price: string;
  imageUrls: string[];
  userId: string;
  createdAt: string;
}

export default function SelectProductForVroomModal({ 
  isOpen, 
  onClose, 
  vroomId 
}: SelectProductForVroomModalProps) {
  const { toast } = useToast();
  const { user } = useAuth();
  const queryClient = useQueryClient();
  const [selectedProducts, setSelectedProducts] = useState<string[]>([]);

  // Fetch user's products
  const { data: userProducts, isLoading } = useQuery<Product[]>({
    queryKey: ["/api/products/user"],
    enabled: isOpen && !!user,
    retry: false,
  });

  // Fetch products already in this vroom
  const { data: vroomProducts } = useQuery({
    queryKey: ["/api/vrooms", vroomId],
    enabled: isOpen && !!vroomId,
    retry: false,
  });

  const addProductsToVroomMutation = useMutation({
    mutationFn: async (productIds: string[]) => {
      const promises = productIds.map(productId => 
        apiRequest("POST", `/api/vrooms/${vroomId}/products`, { productId })
      );
      return await Promise.all(promises);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: `${selectedProducts.length} product${selectedProducts.length > 1 ? 's' : ''} added to vroom successfully!`,
      });
      queryClient.invalidateQueries({ queryKey: ["/api/vrooms", vroomId] });
      queryClient.invalidateQueries({ queryKey: ["/api/vrooms/user"] });
      handleClose();
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error?.message || "Failed to add products to vroom",
        variant: "destructive",
      });
    },
  });

  const handleProductToggle = (productId: string) => {
    setSelectedProducts(prev => 
      prev.includes(productId)
        ? prev.filter(id => id !== productId)
        : [...prev, productId]
    );
  };

  const handleAddProducts = () => {
    if (selectedProducts.length === 0) {
      toast({
        title: "Error",
        description: "Please select at least one product",
        variant: "destructive",
      });
      return;
    }
    addProductsToVroomMutation.mutate(selectedProducts);
  };

  const handleClose = () => {
    setSelectedProducts([]);
    onClose();
  };

  if (!isOpen) return null;

  // Get products already in vroom
  const productsInVroom = (vroomProducts as any)?.products?.map((p: any) => p.id) || [];

  // Filter out products already in vroom
  const availableProducts = userProducts?.filter(product => !productsInVroom.includes(product.id)) || [];

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="max-w-4xl max-h-[80vh] overflow-y-auto" data-testid="select-product-modal">
        <DialogHeader>
          <DialogTitle data-testid="select-product-title">Add Products to Vroom</DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          {isLoading ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {[1, 2, 3].map(i => (
                <Card key={i}>
                  <CardContent className="p-4">
                    <Skeleton className="h-32 w-full mb-3" />
                    <Skeleton className="h-4 w-3/4 mb-2" />
                    <Skeleton className="h-3 w-1/2" />
                  </CardContent>
                </Card>
              ))}
            </div>
          ) : availableProducts.length === 0 ? (
            <Card>
              <CardContent className="p-12 text-center">
                <FaStore className="mx-auto text-6xl text-muted-foreground mb-4" />
                <h3 className="text-lg font-medium mb-2">No products available</h3>
                <p className="text-muted-foreground">
                  {userProducts?.length === 0 
                    ? "You haven't created any products yet. Create your first product to add it to this vroom."
                    : "All your products are already in this vroom."
                  }
                </p>
              </CardContent>
            </Card>
          ) : (
            <>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {availableProducts.map((product) => (
                  <Card 
                    key={product.id}
                    className={`cursor-pointer transition-all hover:shadow-lg ${
                      selectedProducts.includes(product.id) 
                        ? "ring-2 ring-primary bg-primary/5" 
                        : ""
                    }`}
                    onClick={() => handleProductToggle(product.id)}
                    data-testid={`product-card-${product.id}`}
                  >
                    <CardContent className="p-0">
                      {/* Product Image */}
                      <div className="relative h-32 overflow-hidden rounded-t-lg">
                        {product.imageUrls && product.imageUrls.length > 0 ? (
                          <img
                            src={product.imageUrls[0]}
                            alt={product.name}
                            className="w-full h-full object-cover"
                          />
                        ) : (
                          <div className="w-full h-full bg-gradient-to-br from-primary/20 to-accent/20 flex items-center justify-center">
                            <FaStore className="text-2xl text-muted-foreground" />
                          </div>
                        )}

                        {/* Selection indicator */}
                        {selectedProducts.includes(product.id) && (
                          <div className="absolute top-2 right-2 bg-primary text-primary-foreground rounded-full p-2">
                            <FaCheck className="text-xs" />
                          </div>
                        )}
                      </div>

                      {/* Product Info */}
                      <div className="p-3">
                        <h4 className="font-medium text-sm line-clamp-1 mb-1" data-testid="product-name">
                          {product.name}
                        </h4>

                        <div className="flex items-center justify-between">
                          <Badge variant="secondary" className="text-xs">
                            ${parseFloat(product.price).toFixed(2)}
                          </Badge>
                        </div>

                        {product.description && (
                          <p className="text-xs text-muted-foreground mt-2 line-clamp-2">
                            {product.description}
                          </p>
                        )}
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>

              {/* Action buttons */}
              <div className="flex justify-between pt-4 border-t">
                <Button variant="outline" onClick={handleClose} data-testid="button-cancel">
                  Cancel
                </Button>
                <Button 
                  onClick={handleAddProducts}
                  disabled={selectedProducts.length === 0 || addProductsToVroomMutation.isPending}
                  data-testid="button-add-products"
                >
                  {addProductsToVroomMutation.isPending ? (
                    "Adding..."
                  ) : (
                    <>
                      <FaPlus className="mr-2" />
                      Add {selectedProducts.length > 0 ? `${selectedProducts.length} ` : ""}Products
                    </>
                  )}
                </Button>
              </div>
            </>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/pages/signup.tsx.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Link, useNavigate } from "react-router-dom";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Checkbox } from "@/components/ui/checkbox";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Eye, EyeOff, Mail, Lock, User, ArrowRight, Github, Twitter } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

// Form validation schema
const formSchema = z.object({
  firstName: z.string().min(2, { message: "First name must be at least 2 characters" }),
  lastName: z.string().min(2, { message: "Last name must be at least 2 characters" }),
  email: z.string().email({ message: "Please enter a valid email address" }),
  password: z.string()
    .min(8, { message: "Password must be at least 8 characters" })
    .regex(/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)/, { 
      message: "Password must contain at least one uppercase letter, one lowercase letter, and one number" 
    }),
  confirmPassword: z.string(),
  agreeToTerms: z.boolean().refine(val => val === true, {
    message: "You must agree to the terms and conditions",
  }),
}).refine((data) => data.password === data.confirmPassword, {
  message: "Passwords do not match",
  path: ["confirmPassword"],
});

type FormValues = z.infer<typeof formSchema>;

export default function SignUp() {
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const { toast } = useToast();
  const navigate = useNavigate();

  const form = useForm<FormValues>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      firstName: "",
      lastName: "",
      email: "",
      password: "",
      confirmPassword: "",
      agreeToTerms: false,
    },
  });

  async function onSubmit(values: FormValues) {
    setIsLoading(true);

    try {
      const response = await fetch('/api/auth/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          firstName: values.firstName,
          lastName: values.lastName,
          email: values.email,
          password: values.password,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        toast({
          title: "Account created successfully",
          description: "Welcome to our community!",
        });

        // Redirect to login page or dashboard
        navigate('/login');
      } else {
        toast({
          title: "Signup failed",
          description: data.message || "Something went wrong. Please try again.",
          variant: "destructive",
        });
      }
    } catch (error) {
      console.error('Signup error:', error);
      toast({
        title: "Signup failed",
        description: "Network error. Please check your connection and try again.",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  }

  const handleSocialLogin = (provider: string) => {
    // Redirect to social login endpoint
    window.location.href = `/api/auth/${provider}`;
  };

  return (
    <div className="min-h-screen flex">
