import { useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Skeleton } from "@/components/ui/skeleton";
import { Button } from "@/components/ui/button";
import { FaStore, FaPlus } from "react-icons/fa";
import VroomCard from "@/components/vroom/VroomCard";
import CreateVroomModal from "@/components/vroom/CreateVroomModal";

export default function VroomPage() {
  const { data: userVrooms, isLoading: vroomsLoading } = useQuery({
    queryKey: ["/api/my/vrooms"],
    queryFn: async () => {
      const res = await fetch("/api/my/vrooms");
      if (!res.ok) throw new Error("Failed to fetch vrooms");
      return res.json();
    },
  });

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-6">My Vrooms</h2>

      {vroomsLoading ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {[...Array(3)].map((_, i) => (
            <Skeleton key={i} className="h-48 w-full rounded-xl" />
          ))}
        </div>
      ) : userVrooms && Array.isArray(userVrooms) && userVrooms.length > 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {userVrooms.map((vroom: any) => (
            <VroomCard key={vroom.id} vroom={vroom} showFollowButton={false} />
          ))}
        </div>
      ) : (
        <div className="text-center py-16 text-muted-foreground">
          <FaStore className="mx-auto text-5xl mb-4 opacity-30" />
          <h3 className="text-xl font-medium mb-2">No vrooms yet</h3>
          <p className="mb-6">Create your first vroom to organize your products!</p>
          <CreateVroomModal
            trigger={
              <Button className="bg-primary hover:bg-primary/90">
                <FaPlus className="mr-2" />
                Create Your First Vroom
              </Button>
            }
          />
        </div>
      )}
    </div>
  );
}

storage.ts.content:
import {
  users,
  vrooms,
  products,
  productLikes,
  productComments,
  productShares,
  cartItems,
  orders,
  follows,
  vroomFollows,
  messages,
  imageBucket,
  type User,
  type UpsertUser,
  type UpdateProfile,
  type Vroom,
  type InsertVroom,
  type Product,
  type InsertProduct,
  type Order,
  type InsertOrder,
  type Message,
  type InsertMessage,
  type ImageBucket,
  type InsertImageBucket,
  type CartItem,
  type InsertProductComment,
  type ProductComment,
} from "@shared/schema";
import { db } from "./server/db";
import { eq, desc, sql, and, or, like, ilike, not, inArray, isNull } from "drizzle-orm";
import bcrypt from "bcrypt";

export interface IStorage {
  // User operations (required for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;
  createUserWithPassword(userData: any): Promise<User>;
  verifyPassword(email: string, password: string): Promise<User | null>;
  updatePassword(userId: string, newPassword: string): Promise<void>;
  updateProfile(userId: string, profile: UpdateProfile): Promise<User>;
  getUserProfile(userId: string): Promise<{ user: User; followers: number; following: number } | undefined>;

  // Product operations
  createProduct(userId: string, product: InsertProduct): Promise<Product>;
  getProducts(limit?: number, offset?: number): Promise<Product[]>;
  getProduct(id: string): Promise<Product | undefined>;
  getProductsByUser(userId: string): Promise<Product[]>;
  getProductsByVroom(vroomId: string): Promise<Product[]>;
  getTrendingProducts(): Promise<Product[]>;
  searchProducts(query: string): Promise<Product[]>;
  incrementProductViews(productId: string): Promise<void>;

  // Product interactions
  likeProduct(userId: string, productId: string): Promise<void>;
  unlikeProduct(userId: string, productId: string): Promise<void>;
  commentOnProduct(userId: string, productId: string, content: string, parentCommentId?: string): Promise<ProductComment>;
  getProductComments(productId: string): Promise<(ProductComment & { user: User; replies: (ProductComment & { user: User })[] })[]>;
  shareProduct(userId: string, productId: string): Promise<void>;
  getProductStats(productId: string): Promise<{ likes: number; comments: number; shares: number }>;

  // Vroom operations
  createVroom(userId: string, vroom: InsertVroom): Promise<Vroom>;
  getVrooms(limit?: number): Promise<Vroom[]>;
  getVroom(id: string): Promise<Vroom | undefined>;
  getVroomsByUser(userId: string): Promise<Vroom[]>;
  getTrendingVrooms(): Promise<Vroom[]>;
  followVroom(userId: string, vroomId: string): Promise<void>;
  unfollowVroom(userId: string, vroomId: string): Promise<void>;

  // Cart operations
  addToCart(userId: string, productId: string, quantity?: number): Promise<void>;
  removeFromCart(userId: string, productId: string): Promise<void>;
  getCartItems(userId: string): Promise<CartItem[]>;
  clearCart(userId: string): Promise<void>;

  // Order operations
  createOrder(buyerId: string, order: InsertOrder): Promise<Order>;
  getOrders(userId: string): Promise<Order[]>;
  getOrder(id: string): Promise<Order | undefined>;
  updateOrderStatus(orderId: string, status: string): Promise<void>;

  // Message operations
  sendMessage(senderId: string, message: InsertMessage): Promise<Message>;
  getMessages(userId1: string, userId2: string): Promise<Message[]>;
  getConversations(userId: string): Promise<any[]>;
  markMessagesAsRead(userId: string, senderId: string): Promise<void>;
  findConversation(userId1: string, userId2: string): Promise<any>;
  createConversation(userId1: string, userId2: string): Promise<any>;

  // Follow operations
  followUser(followerId: string, followingId: string): Promise<void>;
  unfollowUser(followerId: string, followingId: string): Promise<void>;
  getFollowers(userId: string): Promise<User[]>;
  getFollowing(userId: string): Promise<User[]>;

  // Search operations
  searchUsers(query: string, excludeUserId: string): Promise<User[]>;
  searchVrooms(query: string): Promise<Vroom[]>;

  // Image bucket operations
  uploadImage(userId: string, image: InsertImageBucket): Promise<ImageBucket>;
  getImagesByUser(userId: string): Promise<ImageBucket[]>;
  getImageForOwner(imageId: string, userId: string): Promise<ImageBucket | undefined>;
  getPublicImage(imageId: string): Promise<ImageBucket | undefined>;
  deleteImage(imageId: string, userId: string): Promise<void>;
  updateImageMetadata(imageId: string, userId: string, metadata: Partial<InsertImageBucket>): Promise<ImageBucket>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  async createUserWithPassword(userData: {
    email: string;
    password: string;
    firstName: string;
    lastName: string;
    username?: string;
    bannerImageUrl?: string;
    bio?: string;
    location?: string;
    website?: string;
  }): Promise<User> {
    // Hash the password
    const saltRounds = 10;
    const hashedPassword = await bcrypt.hash(userData.password, saltRounds);

    const [user] = await db
      .insert(users)
      .values({
        ...userData,
        password: hashedPassword,
      })
      .returning();
    return user;
  }

  async verifyPassword(email: string, password: string): Promise<User | null> {
    const user = await this.getUserByEmail(email);
    if (!user || !user.password) {
      return null;
    }

    const isValidPassword = await bcrypt.compare(password, user.password);
    if (!isValidPassword) {
      return null;
    }

    return user;
  }

  async updatePassword(userId: string, newPassword: string): Promise<void> {
    const saltRounds = 10;
    const hashedPassword = await bcrypt.hash(newPassword, saltRounds);

    await db
      .update(users)
      .set({ password: hashedPassword, updatedAt: new Date() })
      .where(eq(users.id, userId));
  }

  async updateProfile(userId: string, profile: UpdateProfile): Promise<User> {
    const [user] = await db
      .update(users)
      .set({ ...profile, updatedAt: new Date() })
      .where(eq(users.id, userId))
      .returning();
    return user;
  }

  async getUserProfile(userId: string): Promise<{ user: User; followers: number; following: number } | undefined> {
    const user = await this.getUser(userId);
    if (!user) return undefined;

    const [followersResult] = await db
      .select({ count: sql<number>`count(*)` })
      .from(follows)
      .where(eq(follows.followingId, userId));

    const [followingResult] = await db
      .select({ count: sql<number>`count(*)` })
      .from(follows)
      .where(eq(follows.followerId, userId));

    return {
      user: { ...user, password: null }, // Hide password in public profile
      followers: Number(followersResult.count),
      following: Number(followingResult.count),
    };
  }

  // Product operations
  async createProduct(userId: string, product: InsertProduct): Promise<Product> {
    const [newProduct] = await db
      .insert(products)
      .values({ ...product, userId })
      .returning();
    return newProduct;
  }

  async getProducts(limit = 20, offset = 0): Promise<Product[]> {
    return await db
      .select()
      .from(products)
      .where(eq(products.isAvailable, true))
      .orderBy(desc(products.createdAt))
      .limit(limit)
      .offset(offset);
  }

  async getProduct(id: string): Promise<Product | undefined> {
    const [product] = await db.select().from(products).where(eq(products.id, id));
    return product;
  }

  async getProductsByUser(userId: string): Promise<Product[]> {
    return await db
      .select()
      .from(products)
      .where(eq(products.userId, userId))
      .orderBy(desc(products.createdAt));
  }


  async getTrendingProducts(): Promise<Product[]> {
    return await db
      .select()
      .from(products)
      .where(eq(products.isAvailable, true))
      .orderBy(desc(products.views))
      .limit(10);
  }

  async searchProducts(query: string): Promise<Product[]> {
    return await db
      .select()
      .from(products)
      .where(
        and(
          eq(products.isAvailable, true),
          or(
            like(products.name, `%${query}%`),
            like(products.description, `%${query}%`),
            sql`${products.hashtags} && ARRAY[${query}]`
          )
        )
      )
      .orderBy(desc(products.createdAt));
  }


  async searchVrooms(query: string): Promise<Vroom[]> {
    return await db
      .select()
      .from(vrooms)
      .where(
        and(
          eq(vrooms.isPublic, true),
          or(
            like(vrooms.name, `%${query}%`),
            like(vrooms.description, `%${query}%`)
          )
        )
      )
      .orderBy(desc(vrooms.createdAt))
      .limit(20);
  }

  async incrementProductViews(productId: string): Promise<void> {
    await db
      .update(products)
      .set({ views: sql`${products.views} + 1` })
      .where(eq(products.id, productId));
  }

  // Product interactions
  async likeProduct(userId: string, productId: string): Promise<void> {
    await db.insert(productLikes).values({ userId, productId }).onConflictDoNothing();
  }

  async unlikeProduct(userId: string, productId: string): Promise<void> {
    await db
      .delete(productLikes)
      .where(and(eq(productLikes.userId, userId), eq(productLikes.productId, productId)));
  }

  async commentOnProduct(userId: string, productId: string, content: string, parentCommentId?: string): Promise<ProductComment> {
    const [comment] = await db.insert(productComments).values({ 
      userId, 
      productId, 
      content, 
      parentCommentId 
    }).returning();
    return comment;
  }

  async getProductComments(productId: string): Promise<(ProductComment & { user: User; replies: (ProductComment & { user: User })[] })[]> {
    // Get top-level comments (no parent)
    const topLevelComments = await db
      .select()
      .from(productComments)
      .leftJoin(users, eq(productComments.userId, users.id))
      .where(and(
        eq(productComments.productId, productId),
        isNull(productComments.parentCommentId) // Only top-level comments
      ))
      .orderBy(desc(productComments.createdAt));

    // Get all replies for these comments
    const commentIds = topLevelComments.map(row => row.product_comments.id);

    let replies: any[] = [];
    if (commentIds.length > 0) {
      replies = await db
        .select()
        .from(productComments)
        .leftJoin(users, eq(productComments.userId, users.id))
        .where(inArray(productComments.parentCommentId, commentIds))
        .orderBy(productComments.createdAt); // Oldest first for replies
    }

    // Group replies by parent comment ID
    const repliesByParent = replies.reduce((acc, row) => {
      const parentId = row.product_comments.parentCommentId;
      if (!acc[parentId]) acc[parentId] = [];
      acc[parentId].push({
        ...row.product_comments,
        user: row.users
      });
      return acc;
    }, {} as Record<string, any[]>);

    // Combine comments with their replies
    return topLevelComments.map(row => ({
      ...row.product_comments,
      user: row.users || { id: '', email: null, password: null, firstName: null, lastName: null, profileImageUrl: null, bannerImageUrl: null, username: null, bio: null, location: null, website: null, createdAt: null, updatedAt: null },
      replies: repliesByParent[row.product_comments.id] || []
    }));
  }

  async shareProduct(userId: string, productId: string): Promise<void> {
    await db.insert(productShares).values({ userId, productId }).onConflictDoNothing();
  }

  async getProductStats(productId: string): Promise<{ likes: number; comments: number; shares: number }> {
    const [likesResult] = await db
      .select({ count: sql<number>`count(*)` })
      .from(productLikes)
      .where(eq(productLikes.productId, productId));

    const [commentsResult] = await db
      .select({ count: sql<number>`count(*)` })
      .from(productComments)
      .where(eq(productComments.productId, productId));

    const [sharesResult] = await db
      .select({ count: sql<number>`count(*)` })
      .from(productShares)
      .where(eq(productShares.productId, productId));

    return {
      likes: Number(likesResult.count),
      comments: Number(commentsResult.count),
      shares: Number(sharesResult.count),
    };
  }

  // Vroom operations
  async createVroom(userId: string, vroom: InsertVroom): Promise<Vroom> {
    const [newVroom] = await db
      .insert(vrooms)
      .values({ ...vroom, userId })
      .returning();
    return newVroom;
  }

  async getVrooms(limit = 20): Promise<Vroom[]> {
    return await db
      .select()
      .from(vrooms)
      .where(eq(vrooms.isPublic, true))
      .orderBy(desc(vrooms.createdAt))
      .limit(limit);
  }

  async getVroom(id: string): Promise<Vroom | undefined> {
    const [vroom] = await db.select().from(vrooms).where(eq(vrooms.id, id));
    return vroom;
  }

  async getVroomsByUser(userId: string): Promise<Vroom[]> {
    return await db
      .select()
      .from(vrooms)
      .where(eq(vrooms.userId, userId))
      .orderBy(desc(vrooms.createdAt));
  }

  async getTrendingVrooms(): Promise<Vroom[]> {
    return await db
      .select({
        id: vrooms.id,
        userId: vrooms.userId,
        name: vrooms.name,
        description: vrooms.description,
        coverImageUrl: vrooms.coverImageUrl,
        isPublic: vrooms.isPublic,
        createdAt: vrooms.createdAt,
        updatedAt: vrooms.updatedAt,
      })
      .from(vrooms)
      .leftJoin(vroomFollows, eq(vrooms.id, vroomFollows.vroomId))
      .where(eq(vrooms.isPublic, true))
      .groupBy(vrooms.id)
      .orderBy(desc(sql`count(${vroomFollows.id})`))
      .limit(10);
  }

  async followVroom(userId: string, vroomId: string): Promise<void> {
    await db.insert(vroomFollows).values({ userId, vroomId }).onConflictDoNothing();
  }

  async unfollowVroom(userId: string, vroomId: string): Promise<void> {
    await db
      .delete(vroomFollows)
      .where(and(eq(vroomFollows.userId, userId), eq(vroomFollows.vroomId, vroomId)));
  }

  async deleteVroom(id: string, userId: string): Promise<void> {
    // First remove vroom reference from products
    await db
      .update(products)
      .set({ vroomId: null })
      .where(eq(products.vroomId, id));

    // Then delete the vroom (only if user owns it)
    await db
      .delete(vrooms)
      .where(and(eq(vrooms.id, id), eq(vrooms.userId, userId)));
  }

  async addProductToVroom(productId: string, vroomId: string, userId: string): Promise<void> {
    // Verify the user owns the vroom
    const vroom = await this.getVroom(vroomId);
    if (!vroom || vroom.userId !== userId) {
      throw new Error("Vroom not found or access denied");
    }

    // Update the product's vroom
    await db
      .update(products)
      .set({ vroomId })
      .where(eq(products.id, productId));
  }

  async removeProductFromVroom(productId: string, vroomId: string, userId: string): Promise<void> {
    // Verify the user owns the vroom
    const vroom = await this.getVroom(vroomId);
    if (!vroom || vroom.userId !== userId) {
      throw new Error("Vroom not found or access denied");
    }

    // Remove the product from the vroom
    await db
      .update(products)
      .set({ vroomId: null })
      .where(and(eq(products.id, productId), eq(products.vroomId, vroomId)));
  }

  async getProductsByVroom(vroomId: string): Promise<Product[]> {
    return await db
      .select()
      .from(products)
      .where(eq(products.vroomId, vroomId))
      .orderBy(desc(products.createdAt));
  }

  async getVroomsByProduct(productId: string): Promise<Vroom[]> {
    const product = await this.getProduct(productId);
    if (!product || !product.vroomId) {
      return [];
    }

    const vroom = await this.getVroom(product.vroomId);
    return vroom ? [vroom] : [];
  }

  async getVroomStats(vroomId: string, userId?: string): Promise<{ followers: number; views: number; isFollowing: boolean }> {
    const [followersResult] = await db
      .select({ count: sql<number>`count(*)` })
      .from(vroomFollows)
      .where(eq(vroomFollows.vroomId, vroomId));

    let isFollowing = false;
    if (userId) {
      const [followResult] = await db
        .select()
        .from(vroomFollows)
        .where(and(eq(vroomFollows.vroomId, vroomId), eq(vroomFollows.userId, userId)));
      isFollowing = !!followResult;
    }

    return {
      followers: Number(followersResult.count),
      views: 0, // TODO: Implement view tracking
      isFollowing,
    };
  }

  // Cart operations
  async addToCart(userId: string, productId: string, quantity = 1): Promise<void> {
    await db
      .insert(cartItems)
      .values({ userId, productId, quantity })
      .onConflictDoUpdate({
        target: [cartItems.userId, cartItems.productId],
        set: { quantity: sql`${cartItems.quantity} + ${quantity}` },
      });
  }

  async removeFromCart(userId: string, productId: string): Promise<void> {
    await db
      .delete(cartItems)
      .where(and(eq(cartItems.userId, userId), eq(cartItems.productId, productId)));
  }

  async getCartItems(userId: string): Promise<CartItem[]> {
    return await db
      .select()
      .from(cartItems)
      .where(eq(cartItems.userId, userId));
  }

  async clearCart(userId: string): Promise<void> {
    await db.delete(cartItems).where(eq(cartItems.userId, userId));
  }

  // Order operations
  async createOrder(buyerId: string, order: InsertOrder): Promise<Order> {
    const [newOrder] = await db
      .insert(orders)
      .values({ ...order, buyerId })
      .returning();
    return newOrder;
  }

  async getOrders(userId: string): Promise<Order[]> {
    return await db
      .select()
      .from(orders)
      .where(or(eq(orders.buyerId, userId), eq(orders.sellerId, userId)))
      .orderBy(desc(orders.createdAt));
  }

  async getOrder(id: string): Promise<Order | undefined> {
    const [order] = await db.select().from(orders).where(eq(orders.id, id));
    return order;
  }

  async updateOrderStatus(orderId: string, status: string): Promise<void> {
    await db
      .update(orders)
      .set({ status, updatedAt: new Date() })
      .where(eq(orders.id, orderId));
  }

  // Message operations
  async sendMessage(senderId: string, message: InsertMessage): Promise<Message> {
    // Validate that the receiver exists
    const receiver = await this.getUser(message.receiverId);
    if (!receiver) {
      throw new Error("Receiver not found");
    }

    // Prevent users from sending messages to themselves
    if (senderId === message.receiverId) {
      throw new Error("Cannot send message to yourself");
    }

    const [newMessage] = await db
      .insert(messages)
      .values({ ...message, senderId })
      .returning();
    return newMessage;
  }

  async getMessages(userId1: string, userId2: string): Promise<Message[]> {
    return await db
      .select()
      .from(messages)
      .where(
        or(
          and(eq(messages.senderId, userId1), eq(messages.receiverId, userId2)),
          and(eq(messages.senderId, userId2), eq(messages.receiverId, userId1))
        )
      )
      .orderBy(messages.createdAt);
  }

  async getConversations(userId: string): Promise<any[]> {
    // Get all users who have sent or received messages with this user
    const conversations = await db
      .selectDistinct({
        userId: sql<string>`CASE WHEN ${messages.senderId} = ${userId} THEN ${messages.receiverId} ELSE ${messages.senderId} END`,
        lastMessage: messages.content,
        lastMessageTime: messages.createdAt,
        isRead: messages.isRead,
      })
      .from(messages)
      .where(or(eq(messages.senderId, userId), eq(messages.receiverId, userId)))
      .orderBy(desc(messages.createdAt));

    return conversations;
  }

  async markMessagesAsRead(userId: string, senderId: string): Promise<void> {
    await db
      .update(messages)
      .set({ isRead: true })
      .where(and(eq(messages.receiverId, userId), eq(messages.senderId, senderId)));
  }

  async findConversation(userId1: string, userId2: string): Promise<any> {
    // Since we don't have a separate conversations table, check if messages exist between users
    const [message] = await db
      .select()
      .from(messages)
      .where(
        or(
          and(eq(messages.senderId, userId1), eq(messages.receiverId, userId2)),
          and(eq(messages.senderId, userId2), eq(messages.receiverId, userId1))
        )
      )
      .limit(1);

    if (message) {
      return { id: `${userId1}-${userId2}`, participants: [userId1, userId2] };
    }
    return null;
  }

  async createConversation(userId1: string, userId2: string): Promise<any> {
    // For our simple messaging system, we don't need to create an actual conversation record
    // We'll return a virtual conversation object
    return { id: `${userId1}-${userId2}`, participants: [userId1, userId2] };
  }

  async searchUsers(query: string, excludeUserId: string): Promise<User[]> {
    const searchTerm = `%${query.toLowerCase()}%`;

    return await db
      .select()
      .from(users)
      .where(
        and(
          not(eq(users.id, excludeUserId)),
          or(
            ilike(users.firstName, searchTerm),
            ilike(users.lastName, searchTerm),
            ilike(users.email, searchTerm),
            ilike(users.username, searchTerm)
          )
        )
      )
      .limit(10);
  }

  // Follow operations
  async followUser(followerId: string, followingId: string): Promise<void> {
    await db.insert(follows).values({ followerId, followingId }).onConflictDoNothing();
  }

  async unfollowUser(followerId: string, followingId: string): Promise<void> {
    await db
      .delete(follows)
      .where(and(eq(follows.followerId, followerId), eq(follows.followingId, followingId)));
  }

  async getFollowers(userId: string): Promise<User[]> {
    return await db
      .select({
        id: users.id,
        email: users.email,
        password: sql<string | null>`null`, // Hide password for public data
        firstName: users.firstName,
        lastName: users.lastName,
        profileImageUrl: users.profileImageUrl,
        bannerImageUrl: users.bannerImageUrl,
        username: users.username,
        bio: users.bio,
        location: users.location,
        website: users.website,
        createdAt: users.createdAt,
        updatedAt: users.updatedAt,
      })
      .from(follows)
      .innerJoin(users, eq(follows.followerId, users.id))
      .where(eq(follows.followingId, userId));
  }

  async getFollowing(userId: string): Promise<User[]> {
    return await db
      .select({
        id: users.id,
        email: users.email,
        password: sql<string | null>`null`, // Hide password for public data
        firstName: users.firstName,
        lastName: users.lastName,
        profileImageUrl: users.profileImageUrl,
        bannerImageUrl: users.bannerImageUrl,
        username: users.username,
        bio: users.bio,
        location: users.location,
        website: users.website,
        createdAt: users.createdAt,
        updatedAt: users.updatedAt,
      })
      .from(follows)
      .innerJoin(users, eq(follows.followingId, users.id))
      .where(eq(follows.followerId, userId));
  }

  // Image bucket operations
  async uploadImage(userId: string, image: InsertImageBucket): Promise<ImageBucket> {
    const [newImage] = await db
      .insert(imageBucket)
      .values({ ...image, userId })
      .returning();
    return newImage;
  }

  async getImagesByUser(userId: string): Promise<ImageBucket[]> {
    return await db
      .select()
      .from(imageBucket)
      .where(eq(imageBucket.userId, userId))
      .orderBy(desc(imageBucket.createdAt));
  }

  async getImageForOwner(imageId: string, userId: string): Promise<ImageBucket | undefined> {
    const [image] = await db
      .select()
      .from(imageBucket)
      .where(and(eq(imageBucket.id, imageId), eq(imageBucket.userId, userId)));
    return image;
  }

  async getPublicImage(imageId: string): Promise<ImageBucket | undefined> {
    const [image] = await db
      .select()
      .from(imageBucket)
      .where(and(eq(imageBucket.id, imageId), eq(imageBucket.isPublic, true)));
    return image;
  }

  async deleteImage(imageId: string, userId: string): Promise<void> {
    await db
      .delete(imageBucket)
      .where(and(eq(imageBucket.id, imageId), eq(imageBucket.userId, userId)));
  }

  async updateImageMetadata(imageId: string, userId: string, metadata: Partial<InsertImageBucket>): Promise<ImageBucket> {
    const [updatedImage] = await db
      .update(imageBucket)
      .set({ ...metadata, updatedAt: new Date() })
      .where(and(eq(imageBucket.id, imageId), eq(imageBucket.userId, userId)))
      .returning();
    return updatedImage;
  }
}

export const storage = new DatabaseStorage();

