import { useState, useEffect } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useCart } from "@/hooks/useCart";
import { useToast } from "@/hooks/use-toast";
import ProductCommentsModal from "@/components/product/ProductCommentsModal";
import { 
  FaHeart, 
  FaRegHeart, 
  FaComment, 
  FaShare, 
  FaBookmark, 
  FaShoppingCart 
} from "react-icons/fa";

interface ProductPostActionsProps {
  product: {
    id: string;
    name: string;
    likes?: number;
    comments?: number;
    shares?: number;
  };
}

export default function ProductPostActions({ product }: ProductPostActionsProps) {
  const [isLiked, setIsLiked] = useState(false);
  const [isBookmarked, setIsBookmarked] = useState(false);
  const [showCommentsModal, setShowCommentsModal] = useState(false);
  const { addToCart } = useCart();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // âœ… Fetch product stats (likes, comments, shares)
  const { data: stats } = useQuery({
    queryKey: ["/api/products", product.id, "stats"],
    queryFn: () =>
      apiRequest("GET", `/api/products/${product.id}/stats`).then((r) => r.json()),
    initialData: {
      likes: product.likes ?? 0,
      comments: product.comments ?? 0,
      shares: product.shares ?? 0,
    },
  });

  // âœ… Fetch initial like/bookmark state
  useEffect(() => {
    async function fetchUserState() {
      try {
        const [liked, bookmarked] = await Promise.all([
          apiRequest("GET", `/api/products/${product.id}/isLiked`).then((r) => r.json()),
          apiRequest("GET", `/api/products/${product.id}/isBookmarked`).then((r) => r.json()),
        ]);
        setIsLiked(liked);
        setIsBookmarked(bookmarked);
      } catch (err) {
        console.error("Failed to fetch user state:", err);
      }
    }
    fetchUserState();
  }, [product.id]);

  // âœ… LIKE mutation with optimistic update
  const likeMutation = useMutation({
    mutationFn: async () => {
      if (isLiked) {
        await apiRequest("DELETE", `/api/products/${product.id}/like`);
      } else {
        await apiRequest("POST", `/api/products/${product.id}/like`);
      }
    },
    onMutate: () => {
      setIsLiked((prev) => !prev);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/products", product.id, "stats"] });
    },
    onError: () => {
      setIsLiked((prev) => !prev); // rollback
      toast({
        title: "Error",
        description: "Failed to like product",
        variant: "destructive",
      });
    },
  });

  // âœ… BOOKMARK mutation with optimistic update
  const bookmarkMutation = useMutation({
    mutationFn: async () => {
      if (isBookmarked) {
        await apiRequest("DELETE", `/api/products/${product.id}/bookmark`);
      } else {
        await apiRequest("POST", `/api/products/${product.id}/bookmark`);
      }
    },
    onMutate: () => {
      setIsBookmarked((prev) => !prev);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/products", product.id, "stats"] });
    },
    onError: () => {
      setIsBookmarked((prev) => !prev); // rollback
      toast({
        title: "Error",
        description: "Failed to update bookmark",
        variant: "destructive",
      });
    },
  });

  // âœ… SHARE
  const handleShare = async () => {
    try {
      const productUrl = `${window.location.origin}/product/${product.id}`;
      await navigator.clipboard.writeText(productUrl);
      toast({
        title: "Link Copied!",
        description: "Product link has been copied to clipboard.",
      });
    } catch (err) {
      toast({
        title: "Error",
        description: "Failed to copy link. Please try again.",
        variant: "destructive",
      });
    }
  };

  // âœ… ADD TO CART
  const handleAddToCart = () => {
    addToCart(product.id);
    toast({
      title: "Added to Cart",
      description: `${product.name} has been added to your cart.`,
    });
  };

  return (
    <>
      <div className="flex gap-2 mb-2">
        {/* Add to Cart */}
        <button
          onClick={handleAddToCart}
          className="flex-1 p-2 rounded-md bg-accent text-accent-foreground hover:bg-accent/90"
          data-testid={`button-add-to-cart-${product.id}`}
          title="Add to Cart"
        >
          <FaShoppingCart className="w-4 h-4 mx-auto" />
        </button>

        {/* Like */}
        <button
          onClick={() => likeMutation.mutate()}
          className="flex-1 p-2 rounded-md border hover:text-accent transition-colors"
          disabled={likeMutation.isPending}
          data-testid={`button-like-${product.id}`}
          title="Like"
        >
          {isLiked ? (
            <FaHeart className="w-4 h-4 mx-auto text-accent" />
          ) : (
            <FaRegHeart className="w-4 h-4 mx-auto" />
          )}
          <span className="ml-1 text-xs">{stats?.likes ?? 0}</span>
        </button>

        {/* Comment */}
        <button
          onClick={() => setShowCommentsModal(true)}
          className="flex-1 p-2 rounded-md border hover:text-primary transition-colors"
          data-testid={`button-comment-${product.id}`}
          title="Comments"
        >
          <FaComment className="w-4 h-4 mx-auto" />
          <span className="ml-1 text-xs">{stats?.comments ?? 0}</span>
        </button>

        {/* Share */}
        <button
          onClick={handleShare}
          className="flex-1 p-2 rounded-md border hover:text-primary transition-colors"
          data-testid={`button-share-${product.id}`}
          title="Share"
        >
          <FaShare className="w-4 h-4 mx-auto" />
          <span className="ml-1 text-xs">{stats?.shares ?? 0}</span>
        </button>

        {/* Bookmark */}
        <button
          onClick={() => bookmarkMutation.mutate()}
          className="flex-1 p-2 rounded-md border hover:text-primary transition-colors"
          data-testid={`button-bookmark-${product.id}`}
          title="Bookmark"
        >
          <FaBookmark
            className={`w-4 h-4 mx-auto ${isBookmarked ? "text-primary" : ""}`}
          />
        </button>
      </div>

      {/* Comments Modal */}
      {showCommentsModal && (
        <ProductCommentsModal
          isOpen={showCommentsModal}
          onClose={() => setShowCommentsModal(false)}
          product={product}
        />
      )}
    </>
  );
}

client/src/pages/chatbot.tsx.content:
import { useState } from "react";
import { useNavigate } from "react-router-dom";

interface Message {
  sender: "user" | "bot";
  text: string;
}

// Eldady company info
const companyInfo = {
  services:
    "Eldady provides top-quality e-commerce solutions, fast deliveries, and excellent customer support.",
  email: "support@eldady.com",
  whatsapp: "+254712345678",
};

export default function Chatbot() {
  const [messages, setMessages] = useState<Message[]>([
    { sender: "bot", text: "ðŸ‘‹ Hi there! Iâ€™m Stella, Eldadyâ€™s friendly assistant. How can I help you today?" },
  ]);
  const [input, setInput] = useState("");
  const navigate = useNavigate();

  // Simple AI logic
  const stellaReply = (msg: string): string => {
    const text = msg.toLowerCase();

    if (/hello|hi|hey/.test(text)) {
      return "Hello there ðŸ‘‹! Howâ€™s your day going?";
    }
    if (/services|what do you offer|products/.test(text)) {
      return "Great question! ðŸŒŸ " + companyInfo.services;
    }
    if (/bye|goodbye|exit/.test(text)) {
      return "Thanks for chatting with Stella ðŸ’š. Come back anytime!";
    }
    if (/help|support/.test(text)) {
      return `No worries, Iâ€™ve got you ðŸ¤—. You can reach our team via Email: ${companyInfo.email} or WhatsApp: ${companyInfo.whatsapp}`;
    }

    // fallback
    return `Hmm ðŸ¤” Iâ€™m not so sure about that one. But our support staff will gladly help you: Email: ${companyInfo.email}, WhatsApp: ${companyInfo.whatsapp}`;
  };

  const handleSend = () => {
    if (!input.trim()) return;

    const userMsg: Message = { sender: "user", text: input };
    setMessages((prev) => [...prev, userMsg]);

    const reply = stellaReply(input);
    const botMsg: Message = { sender: "bot", text: reply };

    setTimeout(() => {
      setMessages((prev) => [...prev, botMsg]);
    }, 600);

    setInput("");
  };

  return (
    <div className="flex flex-col min-h-screen bg-gray-50">
      {/* Header */}
      <div className="flex items-center justify-between bg-primary text-primary-foreground p-4 shadow">
        <h2 className="font-bold text-lg">Stella - Eldady Assistant</h2>
        <button
          onClick={() => navigate("/")}
          className="text-sm bg-white text-primary px-3 py-1 rounded-md shadow hover:bg-gray-100"
        >
          âœ– Close
        </button>
      </div>

      {/* Chat Messages */}
      <div className="flex-1 p-4 overflow-y-auto space-y-3">
        {messages.map((msg, i) => (
          <div
            key={i}
            className={`max-w-xs px-4 py-2 rounded-lg ${
              msg.sender === "bot"
                ? "bg-gray-200 text-gray-800 self-start"
                : "bg-primary text-primary-foreground self-end ml-auto"
            }`}
          >
            {msg.text}
          </div>
        ))}
      </div>

      {/* Input Area */}
      <div className="p-4 border-t flex items-center space-x-2 bg-white">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && handleSend()}
          placeholder="Type a message..."
          className="flex-1 border rounded-lg px-3 py-2 focus:outline-none focus:ring focus:ring-primary/50"
        />
        <button
          onClick={handleSend}
          className="bg-primary text-primary-foreground px-4 py-2 rounded-lg hover:bg-primary/90 transition"
        >
          Send
        </button>
      </div>
    </div>
  );
}
